const N = PAGES.length;
const step = 360 / N;
let rot = 0;
let targetRot = 0;
let focusedIndex = null;

function build(){
  carousel.innerHTML = "";
  PAGES.forEach((p,i)=>{
    const el = document.createElement("div");
    el.className = "card";
    el.dataset.index = i;
    el.innerHTML = `<h3>${p.title}</h3><p>${p.sub}</p><div class="img"></div>`;
    el.addEventListener("click",()=>handleCardClick(i,p));
    carousel.appendChild(el);
  });
}
build();
const cards = [...document.querySelectorAll(".card")];

function normalize(a){
  let x = ((a%360)+360)%360;
  if(x>180) x-=360;
  return x;
}

function update(){
  const radius = parseFloat(getComputedStyle(document.documentElement).getPropertyValue("--radius3d"));
  cards.forEach((c,i)=>{
    const base = i * step;
    const a = normalize(base + rot);
    c.classList.toggle("front", Math.abs(a)<12);
    c.style.transform = `rotateY(${base}deg) translateZ(${radius}px) rotateY(${-a*0.08}deg)`;
    const t = Math.abs(a)/180;
    c.style.opacity = 0.25 + (1-t)*0.75;
  });
  carousel.style.transform = `rotateY(${rot}deg)`;
}

function tick(){
  rot += (targetRot-rot)*0.18;
  update();
  requestAnimationFrame(tick);
}
tick();

/* scroll + trackpad */
scene.addEventListener("wheel",e=>{
  targetRot += e.deltaY * 0.25;
},{passive:true});

/* touch */
let startX=null;
scene.addEventListener("touchstart",e=>startX=e.touches[0].clientX,{passive:true});
scene.addEventListener("touchmove",e=>{
  if(startX===null) return;
  const dx=e.touches[0].clientX-startX;
  targetRot -= dx*0.4;
  startX=e.touches[0].clientX;
},{passive:true});
scene.addEventListener("touchend",()=>startX=null);

/* two-step click logic */
function handleCardClick(index,page){
  if(focusedIndex!==index){
    focusedIndex=index;
    targetRot = -(index*step);
  } else {
    openPage(page);
  }
}

function openPage(p){
  overlay.classList.add("open");
  panelTitle.textContent = p.title;
  panelSub.textContent = p.sub;
  panelBody.innerHTML = `<p style="line-height:1.9">${p.text}</p>`;
}

function close(){
  overlay.classList.remove("open");
  focusedIndex=null;
}
overlayBg.addEventListener("click",close);
closeBtn.addEventListener("click",close);
